package dsa;
import java.util.Arrays;

public class ArraySortnew {

	public static void main(String args[]) {
		// TODO Auto-generated method stub
		int arr[] = {21, 5, 24, 58};
		String arr2[] = {"Saroj", "Madan", "New"};
		Arrays.sort(arr);
		Arrays.sort(arr2);
		System.out.println("Sort of int arr " + Arrays.toString(arr));
		System.out.println("Sort of string array "+Arrays.toString(arr2));

	}

}
